// src/FormularioProducto.jsx
import React, { useState } from "react";
import axios from "axios";

const FormularioProducto = ({ onProductoAgregado }) => {
  const [formData, setFormData] = useState({
    nombre: "",
    descripcion: "",
    precio: "",
    stock: "",
    moneda: "ARS",
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post("http://localhost:5000/api/productos", formData);
      onProductoAgregado(); // para actualizar la lista en App
      setFormData({
        nombre: "",
        descripcion: "",
        precio: "",
        stock: "",
        moneda: "ARS",
      });
    } catch (err) {
      console.error("❌ Error al crear producto", err);
    }
  };

  return (
    <form onSubmit={handleSubmit} className="bg-white p-4 rounded shadow mb-8">
      <h2 className="text-xl font-semibold mb-4">Nuevo Producto</h2>
      <input type="text" name="nombre" placeholder="Nombre" value={formData.nombre} onChange={handleChange} className="w-full mb-2 p-2 border rounded" required />
      <input type="text" name="descripcion" placeholder="Descripción" value={formData.descripcion} onChange={handleChange} className="w-full mb-2 p-2 border rounded" />
      <input type="number" name="precio" placeholder="Precio" value={formData.precio} onChange={handleChange} className="w-full mb-2 p-2 border rounded" required />
      <input type="number" name="stock" placeholder="Stock" value={formData.stock} onChange={handleChange} className="w-full mb-2 p-2 border rounded" required />
      <select name="moneda" value={formData.moneda} onChange={handleChange} className="w-full mb-4 p-2 border rounded">
        <option value="ARS">ARS</option>
        <option value="USD">USD</option>
      </select>
      <button type="submit" className="bg-blue-600 text-white px-4 py-2 rounded w-full">Guardar Producto</button>
    </form>
  );
};

export default FormularioProducto;
