// === FRONTEND CON FORMULARIO Y LISTADO DE PRODUCTOS ===

// App.jsx
import React, { useState, useEffect } from "react";
import axios from "axios";

function App() {
  const [productos, setProductos] = useState([]);
  const [formData, setFormData] = useState({
    nombre: "",
    descripcion: "",
    precio: "",
    stock: "",
    moneda: "ARS"
  });

  const fetchProductos = async () => {
    try {
      const res = await axios.get("http://localhost:5000/api/productos");
      setProductos(res.data);
    } catch (err) {
      console.error("Error al obtener productos", err);
    }
  };

  useEffect(() => {
    fetchProductos();
  }, []);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post("http://localhost:5000/api/productos", formData);
      fetchProductos();
      setFormData({ nombre: "", descripcion: "", precio: "", stock: "", moneda: "ARS" });
    } catch (err) {
      console.error("Error al crear producto", err);
    }
  };

  return (
    <div className="min-h-screen bg-gray-100 p-8">
      <h1 className="text-3xl font-bold mb-6 text-center text-blue-700">Sistema de Facturación</h1>

      <form onSubmit={handleSubmit} className="bg-white p-6 rounded shadow-md max-w-md mx-auto mb-10">
        <h2 className="text-xl font-semibold mb-4">Nuevo Producto</h2>
        <input type="text" name="nombre" placeholder="Nombre" value={formData.nombre} onChange={handleChange} className="w-full mb-2 p-2 border rounded" required />
        <input type="text" name="descripcion" placeholder="Descripción (opcional)" value={formData.descripcion} onChange={handleChange} className="w-full mb-2 p-2 border rounded" />
        <input type="number" name="precio" placeholder="Precio" value={formData.precio} onChange={handleChange} className="w-full mb-2 p-2 border rounded" required />
        <input type="number" name="stock" placeholder="Stock" value={formData.stock} onChange={handleChange} className="w-full mb-2 p-2 border rounded" required />
        <select name="moneda" value={formData.moneda} onChange={handleChange} className="w-full mb-4 p-2 border rounded">
          <option value="ARS">ARS</option>
          <option value="USD">USD</option>
        </select>
        <button type="submit" className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700 w-full">Guardar Producto</button>
      </form>

      <div className="max-w-2xl mx-auto bg-white p-6 rounded shadow">
        <h2 className="text-xl font-semibold mb-4">Listado de Productos</h2>
        {productos.length === 0 ? (
          <p className="text-gray-500">No hay productos cargados.</p>
        ) : (
          <ul className="divide-y divide-gray-200">
            {productos.map((p) => (
              <li key={p._id} className="py-2">
                <strong>{p.nombre}</strong> – {p.precio} {p.moneda} ({p.stock} en stock)
              </li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
}

export default App;
