import React, { useState, useEffect } from "react";
import axios from "axios";

function FormularioVenta({ onVentaRegistrada }) {
  const [productos, setProductos] = useState([]);
  const [formData, setFormData] = useState({
    productoId: "",
    cantidad: 1,
  });

  useEffect(() => {
    const fetchProductos = async () => {
      try {
        const res = await axios.get("/api/productos");
        setProductos(res.data);
      } catch (err) {
        console.error("❌ Error al cargar productos", err);
      }
    };
    fetchProductos();
  }, []);

  const handleChange = (e) => {
    setFormData((prev) => ({ ...prev, [e.target.name]: e.target.value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post("/api/ventas", {
        productoId: formData.productoId,
        cantidad: parseInt(formData.cantidad),
      });
      alert("✅ Venta registrada correctamente");
      setFormData({ productoId: "", cantidad: 1 });
      onVentaRegistrada?.(); // Para refrescar si es necesario
    } catch (err) {
      console.error("❌ Error al registrar venta", err);
      alert("❌ Error al registrar venta");
    }
  };

  return (
    <form onSubmit={handleSubmit} className="bg-white p-6 rounded shadow-md max-w-md mx-auto mb-10">
      <h2 className="text-xl font-semibold mb-4">Registrar Venta</h2>

      <select
        name="productoId"
        value={formData.productoId}
        onChange={handleChange}
        className="w-full mb-2 p-2 border rounded"
        required
      >
        <option value="">Seleccionar producto</option>
        {productos.map((p) => (
          <option key={p._id} value={p._id}>
            {p.nombre} – {p.precio} {p.moneda} ({p.stock} en stock)
          </option>
        ))}
      </select>

      <input
        type="number"
        name="cantidad"
        placeholder="Cantidad"
        value={formData.cantidad}
        onChange={handleChange}
        className="w-full mb-4 p-2 border rounded"
        min={1}
        required
      />

      <button type="submit" className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700 w-full">
        Registrar Venta
      </button>
    </form>
  );
}

export default FormularioVenta;
