import React, { useState } from "react";
import axios from "axios";

function FormularioVenta({ productos, fetchProductos }) {
  const [ventaData, setVentaData] = useState({
    productoId: "",
    cantidad: 1,
  });

  const handleChange = (e) => {
    setVentaData({ ...ventaData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      await axios.post("http://localhost:5000/api/ventas", ventaData);
      alert("✅ Venta registrada correctamente");
      setVentaData({ productoId: "", cantidad: 1 });
      fetchProductos();
    } catch (err) {
      console.error("❌ Error al registrar venta", err);
      alert(`❌ Error al registrar venta: ${err.response?.data?.error || err.message}`);
    }
  };

  return (
    <form onSubmit={handleSubmit} className="bg-white p-6 rounded shadow-md max-w-md mx-auto mb-10">
      <h2 className="text-xl font-semibold mb-4">Registrar Venta</h2>

      <select
        name="productoId"
        value={ventaData.productoId}
        onChange={handleChange}
        className="w-full mb-2 p-2 border rounded"
        required
      >
        <option value="">Seleccionar producto</option>
        {productos.map((p) => (
          <option key={p._id} value={p._id}>
            {p.nombre} – {p.precio} {p.moneda} ({p.stock} en stock)
          </option>
        ))}
      </select>

      <input
        type="number"
        name="cantidad"
        placeholder="Cantidad"
        value={ventaData.cantidad}
        onChange={handleChange}
        min="1"
        className="w-full mb-4 p-2 border rounded"
        required
      />

      <button type="submit" className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700 w-full">
        Registrar Venta
      </button>
    </form>
  );
}

export default FormularioVenta;
