import React, { useEffect, useState } from "react";
import axios from "axios";

function HistorialVentas() {
  const [ventas, setVentas] = useState([]);
  const [filtroFecha, setFiltroFecha] = useState("");

  const fetchVentas = async () => {
    try {
      const res = await axios.get("http://localhost:5000/api/ventas", {
        params: filtroFecha ? { fecha: filtroFecha } : {}
      });
      setVentas(res.data);
    } catch (err) {
      console.error("Error al cargar ventas", err);
    }
  };

  useEffect(() => {
    fetchVentas();
  }, [filtroFecha]);

  return (
    <div className="max-w-3xl mx-auto mt-10">
      <h1 className="text-2xl font-bold mb-6 text-blue-600 text-center">Historial de Ventas</h1>

      <input
        type="date"
        value={filtroFecha}
        onChange={(e) => setFiltroFecha(e.target.value)}
        className="block mb-6 mx-auto p-2 border rounded"
      />

      {ventas.length === 0 ? (
        <p className="text-gray-500 text-center">No hay ventas registradas.</p>
      ) : (
        <table className="w-full bg-white shadow-md rounded">
          <thead>
            <tr className="bg-blue-100 text-left">
              <th className="p-2">Fecha</th>
              <th className="p-2">Producto</th>
              <th className="p-2">Cantidad</th>
              <th className="p-2">Precio</th>
              <th className="p-2">Total</th>
              <th className="p-2">Moneda</th>
            </tr>
          </thead>
          <tbody>
            {ventas.map((venta) => (
              <tr key={venta._id} className="border-t">
                <td className="p-2">{new Date(venta.fecha).toLocaleDateString()}</td>
                <td className="p-2">{venta.producto?.nombre}</td>
                <td className="p-2">{venta.cantidad}</td>
                <td className="p-2">{venta.precioUnitario}</td>
                <td className="p-2 font-semibold">{venta.total}</td>
                <td className="p-2">{venta.moneda}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}

export default HistorialVentas;
