import { useEffect, useState } from "react";
import axios from "axios";

const FormularioVenta = () => {
  const [productos, setProductos] = useState([]);
  const [formData, setFormData] = useState({
    cliente: { nombre: "" },
    productos: [],
    moneda: "ARS",
    sucursal: "Sucursal Central",
  });

  const [productoSeleccionado, setProductoSeleccionado] = useState("");
  const [cantidad, setCantidad] = useState(1);
  const [mensaje, setMensaje] = useState("");

  useEffect(() => {
    axios.get("/api/productos").then((res) => setProductos(res.data));
  }, []);

  const agregarProducto = () => {
    if (!productoSeleccionado || cantidad <= 0) return;

    const producto = productos.find((p) => p._id === productoSeleccionado);
    setFormData((prev) => ({
      ...prev,
      productos: [
        ...prev.productos,
        {
          productoId: producto._id,
          cantidad: Number(cantidad),
          precioUnitario: producto.precio,
        },
      ],
    }));

    setProductoSeleccionado("");
    setCantidad(1);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post("/api/ventas", formData);
      setMensaje("✅ Venta registrada correctamente.");
      setFormData({
        cliente: { nombre: "" },
        productos: [],
        moneda: "ARS",
        sucursal: "Sucursal Central",
      });
    } catch (err) {
      console.error(err);
      setMensaje("❌ Error al registrar la venta.");
    }
  };

  return (
    <div className="bg-zinc-900 p-6 rounded-lg mb-6">
      <h2 className="text-lg font-bold text-white mb-4">Registrar Venta</h2>
      <form onSubmit={handleSubmit} className="space-y-3">
        <input
          type="text"
          placeholder="Nombre del cliente"
          className="w-full p-2 bg-zinc-800 text-white rounded"
          value={formData.cliente.nombre}
          onChange={(e) =>
            setFormData({ ...formData, cliente: { nombre: e.target.value } })
          }
        />
        <div className="flex gap-2">
          <select
            className="w-full p-2 bg-zinc-800 text-white rounded"
            value={productoSeleccionado}
            onChange={(e) => setProductoSeleccionado(e.target.value)}
          >
            <option value="">Seleccionar producto</option>
            {productos.map((prod) => (
              <option key={prod._id} value={prod._id}>
                {prod.nombre} – {prod.stock} disponibles
              </option>
            ))}
          </select>
          <input
            type="number"
            className="w-24 p-2 bg-zinc-800 text-white rounded"
            placeholder="Cantidad"
            value={cantidad}
            onChange={(e) => setCantidad(e.target.value)}
          />
          <button
            type="button"
            onClick={agregarProducto}
            className="bg-blue-600 px-3 text-white rounded"
          >
            Agregar
          </button>
        </div>

        <select
          className="w-full p-2 bg-zinc-800 text-white rounded"
          value={formData.moneda}
          onChange={(e) => setFormData({ ...formData, moneda: e.target.value })}
        >
          <option value="ARS">ARS</option>
          <option value="USD">USD</option>
        </select>

        <input
          type="text"
          placeholder="Sucursal"
          className="w-full p-2 bg-zinc-800 text-white rounded"
          value={formData.sucursal}
          onChange={(e) =>
            setFormData({ ...formData, sucursal: e.target.value })
          }
        />

        <button
          type="submit"
          className="w-full bg-green-600 p-2 rounded text-white font-semibold"
        >
          Registrar Venta
        </button>
      </form>

      {formData.productos.length > 0 && (
        <div className="text-sm text-white mt-4">
          <strong>Productos en la venta:</strong>
          <ul className="list-disc ml-6">
            {formData.productos.map((p, i) => {
              const prod = productos.find((x) => x._id === p.productoId);
              return (
                <li key={i}>
                  {prod?.nombre || "Producto"} – {p.cantidad} unidades
                </li>
              );
            })}
          </ul>
        </div>
      )}

      {mensaje && (
        <div className="mt-4 text-white text-sm font-semibold">{mensaje}</div>
      )}
    </div>
  );
};

export default FormularioVenta;
