import React, { useState, useEffect } from "react";
import axios from "axios";

const FormularioVenta = ({ productos, fetchProductos }) => {
  const [formData, setFormData] = useState({
    items: [],
    cliente: "",
    vendedor: "",
    moneda: "ARS",
    sucursal: "Sucursal 1"
  });

  const [nuevoItem, setNuevoItem] = useState({ producto: "", cantidad: "" });
  const [clientes, setClientes] = useState([]);
  const [vendedores, setVendedores] = useState([]);

  useEffect(() => {
    const fetchClientes = async () => {
      try {
        const res = await axios.get("http://localhost:5000/api/clientes");
        setClientes(res.data);
      } catch (err) {
        console.error("❌ Error al cargar clientes", err);
      }
    };

    const fetchVendedores = async () => {
      try {
        const res = await axios.get("http://localhost:5000/api/vendedores");
        setVendedores(res.data);
      } catch (err) {
        console.error("❌ Error al cargar vendedores", err);
      }
    };

    fetchClientes();
    fetchVendedores();
  }, []);

  const handleItemChange = (e) => {
    setNuevoItem({ ...nuevoItem, [e.target.name]: e.target.value });
  };

  const agregarItem = () => {
    if (!nuevoItem.producto || !nuevoItem.cantidad) return;
    setFormData({
      ...formData,
      items: [...formData.items, { ...nuevoItem }]
    });
    setNuevoItem({ producto: "", cantidad: "" });
  };

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post("http://localhost:5000/api/ventas", formData);
      alert("✅ Venta registrada");
      setFormData({
        items: [],
        cliente: "",
        vendedor: "",
        moneda: "ARS",
        sucursal: "Sucursal 1"
      });
      fetchProductos();
    } catch (err) {
      console.error("❌ Error al registrar venta", err);
    }
  };

  const calcularTotal = () => {
    let total = 0;
    formData.items.forEach((item) => {
      const prod = productos.find((p) => p._id === item.producto);
      if (prod) total += prod.precio * item.cantidad;
    });
    return total.toFixed(2);
  };

  return (
    <form onSubmit={handleSubmit} className="bg-white p-6 rounded shadow-md max-w-md mx-auto mb-8">
      <h2 className="text-xl font-semibold mb-4 text-center text-blue-700">Registrar Venta</h2>

      <div className="mb-4">
        <select name="producto" value={nuevoItem.producto} onChange={handleItemChange} className="w-full mb-2 p-2 border rounded">
          <option value="">Seleccionar producto</option>
          {productos.map((p) => (
            <option key={p._id} value={p._id}>
              {p.nombre} - {p.precio} {p.moneda} - Stock: {p.stock}
            </option>
          ))}
        </select>

        <input type="number" name="cantidad" placeholder="Cantidad" value={nuevoItem.cantidad} onChange={handleItemChange} className="w-full mb-2 p-2 border rounded" />

        <button type="button" onClick={agregarItem} className="bg-green-600 text-white px-4 py-2 rounded w-full hover:bg-green-700">
          Agregar Producto
        </button>
      </div>

      <ul className="mb-4">
        {formData.items.map((item, idx) => {
          const p = productos.find((prod) => prod._id === item.producto);
          return (
            <li key={idx} className="text-sm mb-1">
              {p?.nombre} x {item.cantidad} = {p ? p.precio * item.cantidad : 0} {p?.moneda || ""}
            </li>
          );
        })}
      </ul>

      <p className="font-semibold mb-4">Total: {calcularTotal()} {formData.moneda}</p>

      <select name="cliente" value={formData.cliente} onChange={handleChange} required className="w-full mb-2 p-2 border rounded">
        <option value="">Seleccionar cliente</option>
        {clientes.map((c) => (
          <option key={c._id} value={c._id}>
            {c.nombre} {c.apellido}
          </option>
        ))}
      </select>

      <select name="vendedor" value={formData.vendedor} onChange={handleChange} required className="w-full mb-2 p-2 border rounded">
        <option value="">Seleccionar vendedor</option>
        {vendedores.map((v) => (
          <option key={v._id} value={v._id}>
            {v.nombre} {v.apellido}
          </option>
        ))}
      </select>

      <select name="moneda" value={formData.moneda} onChange={handleChange} className="w-full mb-2 p-2 border rounded">
        <option value="ARS">ARS</option>
        <option value="USD">USD</option>
      </select>

      <input type="text" name="sucursal" placeholder="Sucursal" value={formData.sucursal} onChange={handleChange} className="w-full mb-4 p-2 border rounded" />

      <button type="submit" className="bg-blue-600 text-white px-4 py-2 rounded w-full hover:bg-blue-700">
        Guardar Venta
      </button>
    </form>
  );
};

export default FormularioVenta;
