import React from "react";
import { BrowserRouter as Router, Routes, Route, Link } from "react-router-dom";
import FormularioProducto from "./FormularioProducto";
import FormularioVenta from "./FormularioVenta";
import HistorialVentas from "./HistorialVentas"; // Asegurate que exista

function App() {
  return (
    <Router>
      <div className="min-h-screen bg-gray-100">
        <nav className="bg-white shadow p-4 flex justify-center space-x-6 mb-6 text-blue-700 font-semibold text-lg">
          <Link to="/">ðŸ“¦ Productos</Link>
          <Link to="/ventas">ðŸ’¸ Ventas</Link>
          <Link to="/historial">ðŸ“Š Historial</Link>
        </nav>

        <div className="px-4">
          <Routes>
            <Route
              path="/"
              element={
                <div className="max-w-2xl mx-auto">
                  <h1 className="text-2xl font-bold mb-4 text-blue-700">GestiÃ³n de Productos</h1>
                  <FormularioProducto />
                </div>
              }
            />
            <Route
              path="/ventas"
              element={
                <div className="max-w-2xl mx-auto">
                  <h1 className="text-2xl font-bold mb-4 text-blue-700">Registrar Venta</h1>
                  <FormularioVenta />
                </div>
              }
            />
            <Route
              path="/historial"
              element={
                <div className="max-w-3xl mx-auto">
                  <h1 className="text-2xl font-bold mb-4 text-blue-700">Historial de Ventas</h1>
                  <HistorialVentas />
                </div>
              }
            />
          </Routes>
        </div>
      </div>
    </Router>
  );
}

export default App;
