import React, { useState, useEffect } from "react";
import axios from "axios";

const FormularioVenta = ({ productos, fetchProductos }) => {
  const [formData, setFormData] = useState({
    cliente: "",
    vendedor: "",
    moneda: "ARS",
    sucursal: "Sucursal 1",
    items: [{ producto: "", cantidad: "" }]
  });

  const [clientes, setClientes] = useState([]);
  const [vendedores, setVendedores] = useState([]);

  useEffect(() => {
    const fetchClientes = async () => {
      try {
        const res = await axios.get("/api/clientes");
        setClientes(res.data);
      } catch (err) {
        console.error("❌ Error al cargar clientes", err);
      }
    };

    const fetchVendedores = async () => {
      try {
        const res = await axios.get("/api/vendedores");
        setVendedores(res.data);
      } catch (err) {
        console.error("❌ Error al cargar vendedores", err);
      }
    };

    fetchClientes();
    fetchVendedores();
  }, []);

  const handleItemChange = (index, e) => {
    const newItems = [...formData.items];
    newItems[index][e.target.name] = e.target.value;
    setFormData({ ...formData, items: newItems });
  };

  const agregarItem = () => {
    setFormData({ ...formData, items: [...formData.items, { producto: "", cantidad: "" }] });
  };

  const eliminarItem = (index) => {
    const newItems = formData.items.filter((_, i) => i !== index);
    setFormData({ ...formData, items: newItems });
  };

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post("/api/ventas", {
        cliente: formData.cliente,
        vendedor: formData.vendedor,
        moneda: formData.moneda,
        sucursal: formData.sucursal,
        items: formData.items
      });
      alert("✅ Venta registrada");
      setFormData({
        cliente: "",
        vendedor: "",
        moneda: "ARS",
        sucursal: "Sucursal 1",
        items: [{ producto: "", cantidad: "" }]
      });
      fetchProductos();
    } catch (err) {
      console.error("❌ Error al registrar venta", err);
      alert(err.response?.data?.error || "No se pudo registrar la venta");
    }
  };

  return (
    <form onSubmit={handleSubmit} className="bg-white p-6 rounded shadow-md max-w-md mx-auto mb-8">
      <h2 className="text-xl font-semibold mb-4 text-center text-blue-700">Registrar Venta</h2>

      {formData.items.map((item, index) => {
        const productoSeleccionado = productos.find((p) => p._id === item.producto);
        return (
          <div key={index} className="mb-4 border p-3 rounded">
            <label className="block mb-1">Producto {index + 1}</label>
            <select
              name="producto"
              value={item.producto}
              onChange={(e) => handleItemChange(index, e)}
              required
              className="w-full mb-2 p-2 border rounded"
            >
              <option value="">Seleccionar producto</option>
              {productos.map((p) => (
                <option key={p._id} value={p._id}>
                  {p.nombre} – Stock: {p.stock}
                </option>
              ))}
            </select>

            {item.producto && (
              <p className="text-sm text-gray-600 mb-2">
                Stock disponible: {productoSeleccionado?.stock ?? "N/D"}
              </p>
            )}

            <input
              type="number"
              name="cantidad"
              placeholder="Cantidad"
              value={item.cantidad}
              onChange={(e) => handleItemChange(index, e)}
              required
              className="w-full mb-2 p-2 border rounded"
              min="1"
            />

            {formData.items.length > 1 && (
              <button
                type="button"
                onClick={() => eliminarItem(index)}
                className="text-red-600 text-sm"
              >
                Eliminar producto
              </button>
            )}
          </div>
        );
      })}

      <button
        type="button"
        onClick={agregarItem}
        className="bg-gray-200 text-sm px-3 py-1 rounded hover:bg-gray-300 mb-4"
      >
        + Agregar otro producto
      </button>

      <select name="cliente" value={formData.cliente} onChange={handleChange} required className="w-full mb-2 p-2 border rounded">
        <option value="">Seleccionar cliente</option>
        {clientes.map((c) => (
          <option key={c._id} value={c._id}>
            {(c.nombre || "Sin nombre")} {(c.apellido || "")}
          </option>
        ))}
      </select>

      <select name="vendedor" value={formData.vendedor} onChange={handleChange} required className="w-full mb-2 p-2 border rounded">
        <option value="">Seleccionar vendedor</option>
        {vendedores.map((v) => (
          <option key={v._id} value={v._id}>
            {(v.nombre || "Sin nombre")} {(v.apellido || "")}
          </option>
        ))}
      </select>

      <select name="moneda" value={formData.moneda} onChange={handleChange} className="w-full mb-2 p-2 border rounded">
        <option value="ARS">ARS</option>
        <option value="USD">USD</option>
      </select>

      <input
        type="text"
        name="sucursal"
        placeholder="Sucursal"
        value={formData.sucursal}
        onChange={handleChange}
        className="w-full mb-4 p-2 border rounded"
      />

      <button type="submit" className="bg-blue-600 text-white px-4 py-2 rounded w-full hover:bg-blue-700">
        Guardar Venta
      </button>
    </form>
  );
};

export default FormularioVenta;
