import React, { useEffect, useState } from "react";
import axios from "axios";

const HistorialVentas = () => {
  const [ventas, setVentas] = useState([]);
  const [cargando, setCargando] = useState(true);

  useEffect(() => {
    const obtenerVentas = async () => {
      try {
        const res = await axios.get("/api/ventas");
        setVentas(res.data);
      } catch (err) {
        console.error("❌ Error al obtener ventas:", err);
      } finally {
        setCargando(false);
      }
    };

    obtenerVentas();
  }, []);

  if (cargando) return <p>Cargando historial de ventas...</p>;

  return (
    <div>
      <h2>Historial de Ventas</h2>
      <table>
      <thead className="bg-gray-800 text-white text-sm">
  <tr>
    <th className="px-2 py-1">Fecha</th>
    <th className="px-2 py-1">Cliente</th>
    <th className="px-2 py-1">Vendedor</th>
    <th className="px-2 py-1">Producto</th>
    <th className="px-2 py-1">Cantidad</th>
    <th className="px-2 py-1">Moneda</th>
    <th className="px-2 py-1">Sucursal</th>
  </tr>
</thead>
<tbody>
  {ventas.map((venta) => (
    <React.Fragment key={venta._id}>
      {venta.productos.map((item, index) => (
        <tr key={index} className="border-t text-center text-sm text-white">
          <td className="px-2 py-1">
            {index === 0 ? new Date(venta.fecha).toLocaleDateString() : ""}
          </td>
          <td className="px-2 py-1">
            {index === 0 ? (venta.cliente?.nombre || venta.cliente || "-") : ""}
          </td>
          <td className="px-2 py-1">
            {index === 0 ? (venta.vendedor?.nombre || venta.vendedor || "-") : ""}
          </td>
          <td className="px-2 py-1">{item.nombre}</td>
          <td className="px-2 py-1">{item.cantidad}</td>
          <td className="px-2 py-1">{venta.moneda}</td>
          <td className="px-2 py-1">{venta.sucursal}</td>
        </tr>
      ))}
    </React.Fragment>
  ))}
</tbody>



      </table>
    </div>
  );
};

export default HistorialVentas;
