import React, { useEffect, useState } from "react";
import axios from "axios";

function FormularioVenta() {
  const [productos, setProductos] = useState([]);
  const [venta, setVenta] = useState({
    productos: [],
    sucursal: "Principal",
    moneda: "ARS"
  });

  useEffect(() => {
    axios.get("http://localhost:5000/api/productos")
      .then(res => setProductos(res.data))
      .catch(err => console.error("Error al obtener productos", err));
  }, []);

  const handleProductoChange = (index, field, value) => {
    const nuevosProductos = [...venta.productos];
    nuevosProductos[index][field] = value;
    setVenta({ ...venta, productos: nuevosProductos });
  };

  const agregarProducto = () => {
    setVenta({
      ...venta,
      productos: [...venta.productos, { producto: "", cantidad: 1 }]
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post("http://localhost:5000/api/ventas", venta);
      alert("✅ Venta registrada");
      setVenta({ productos: [], sucursal: "Principal", moneda: "ARS" });
    } catch (err) {
      console.error("❌ Error al registrar venta", err);
      alert("❌ Error al registrar venta: " + err.response?.data?.error || err.message);
    }
  };

  return (
    <form onSubmit={handleSubmit} className="bg-white p-6 rounded shadow-md mb-10">
      <h2 className="text-xl font-semibold mb-4">Registrar Venta</h2>

      {venta.productos.map((item, index) => (
        <div key={index} className="flex items-center gap-2 mb-3">
          <select
            className="flex-1 p-2 border rounded"
            value={item.producto}
            onChange={(e) => handleProductoChange(index, "producto", e.target.value)}
            required
          >
            <option value="">Seleccionar producto</option>
            {productos.map((p) => (
              <option key={p._id} value={p._id}>
                {p.nombre} ({p.stock} disponibles)
              </option>
            ))}
          </select>
          <input
            type="number"
            min={1}
            className="w-24 p-2 border rounded"
            value={item.cantidad}
            onChange={(e) => handleProductoChange(index, "cantidad", e.target.value)}
            required
          />
        </div>
      ))}

      <button
        type="button"
        className="bg-gray-300 hover:bg-gray-400 text-black px-3 py-1 rounded mb-4"
        onClick={agregarProducto}
      >
        + Agregar producto
      </button>

      <div className="mb-3">
        <label className="block mb-1">Sucursal</label>
        <input
          type="text"
          value={venta.sucursal}
          onChange={(e) => setVenta({ ...venta, sucursal: e.target.value })}
          className="w-full p-2 border rounded"
        />
      </div>

      <div className="mb-3">
        <label className="block mb-1">Moneda</label>
        <select
          value={venta.moneda}
          onChange={(e) => setVenta({ ...venta, moneda: e.target.value })}
          className="w-full p-2 border rounded"
        >
          <option value="ARS">ARS</option>
          <option value="USD">USD</option>
        </select>
      </div>

      <button type="submit" className="bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded w-full">
        Registrar Venta
      </button>
    </form>
  );
}

export default FormularioVenta;
