// src/App.jsx
import React, { useEffect, useState } from "react";
import axios from "axios";
import FormularioProducto from "./FormularioProducto";
import FormularioVenta from "./FormularioVenta";
import ListadoProductos from "./ListadoProductos";

function App() {
  const [productos, setProductos] = useState([]);

  const fetchProductos = async () => {
    try {
      const res = await axios.get("http://localhost:5000/api/productos");
      setProductos(res.data);
    } catch (err) {
      console.error("❌ Error al obtener productos", err);
    }
  };

  useEffect(() => {
    fetchProductos();
  }, []);

  return (
    <div className="min-h-screen bg-gray-100 p-8">
      <h1 className="text-3xl font-bold mb-6 text-center text-blue-700">Sistema de Facturación</h1>

      <FormularioProducto onProductoAgregado={fetchProductos} />
      <FormularioVenta productos={productos} onVentaExitosa={fetchProductos} />
      <ListadoProductos productos={productos} />
    </div>
  );
}

export default App;
