import React, { useEffect, useState } from "react";
import axios from "axios";

const FormularioVenta = () => {
  const [productos, setProductos] = useState([]);
  const [formData, setFormData] = useState({
    productoId: "",
    cantidad: 1,
    moneda: "ARS"
  });

  const fetchProductos = async () => {
    try {
      const res = await axios.get("/api/productos");
      setProductos(res.data);
    } catch (err) {
      console.error("Error al obtener productos", err);
    }
  };

  useEffect(() => {
    fetchProductos();
  }, []);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post("/api/ventas", formData);
      alert("✅ Venta registrada");
      setFormData({ productoId: "", cantidad: 1, moneda: "ARS" });
      fetchProductos(); // Actualiza el stock
    } catch (err) {
      console.error("❌ Error al registrar venta", err);
      alert("❌ Error al registrar venta");
    }
  };

  return (
    <form onSubmit={handleSubmit} className="bg-white p-4 rounded shadow mt-6 max-w-md mx-auto">
      <h2 className="text-xl font-semibold mb-3">Registrar Venta</h2>
      <select name="productoId" value={formData.productoId} onChange={handleChange} required className="w-full p-2 border rounded mb-2">
        <option value="">Seleccionar producto</option>
        {productos.map((prod) => (
          <option key={prod._id} value={prod._id}>
            {prod.nombre} ({prod.stock} en stock)
          </option>
        ))}
      </select>
      <input
        type="number"
        name="cantidad"
        placeholder="Cantidad"
        value={formData.cantidad}
        onChange={handleChange}
        min="1"
        className="w-full mb-2 p-2 border rounded"
        required
      />
      <select name="moneda" value={formData.moneda} onChange={handleChange} className="w-full mb-4 p-2 border rounded">
        <option value="ARS">ARS</option>
        <option value="USD">USD</option>
      </select>
      <button type="submit" className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700 w-full">
        Registrar Venta
      </button>
    </form>
  );
};

export default FormularioVenta;
