import React, { useState, useEffect } from "react";
import axios from "axios";

function FormularioVenta({ productos, fetchProductos }) {
  const [formData, setFormData] = useState({
    producto: "",
    cantidad: 1,
    cliente: "",
    vendedor: ""
  });

  const [clientes, setClientes] = useState([]);
  const [vendedores, setVendedores] = useState([]);

  useEffect(() => {
    axios.get("http://localhost:5000/api/clientes")
      .then(res => setClientes(res.data))
      .catch(err => console.error("Error al obtener clientes", err));

    axios.get("http://localhost:5000/api/vendedores")
      .then(res => setVendedores(res.data))
      .catch(err => console.error("Error al obtener vendedores", err));
  }, []);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post("http://localhost:5000/api/ventas", formData);
      alert("✅ Venta registrada");
      setFormData({ producto: "", cantidad: 1, cliente: "", vendedor: "" });
      fetchProductos();
    } catch (err) {
      console.error("❌ Error al registrar venta", err);
      alert("❌ Error al registrar venta");
    }
  };

  return (
    <form onSubmit={handleSubmit} className="bg-white p-6 rounded shadow max-w-md mx-auto mb-10">
      <h2 className="text-xl font-semibold mb-4">Registrar Venta</h2>

      {/* PRODUCTO */}
      <select name="producto" value={formData.producto} onChange={handleChange} className="w-full mb-2 p-2 border rounded" required>
        <option value="">Seleccionar producto</option>
        {productos.map(p => (
          <option key={p._id} value={p._id}>
            {p.nombre} – {p.precio} {p.moneda} ({p.stock} en stock)
          </option>
        ))}
      </select>

      {/* CANTIDAD */}
      <input type="number" name="cantidad" placeholder="Cantidad" value={formData.cantidad} onChange={handleChange} min={1} className="w-full mb-2 p-2 border rounded" required />

      {/* CLIENTE */}
      <select name="cliente" value={formData.cliente} onChange={handleChange} className="w-full mb-2 p-2 border rounded" required>
        <option value="">Seleccionar cliente</option>
        {clientes.map(c => (
          <option key={c._id} value={c._id}>
            {c.nombreApellido} – {c.celular}
          </option>
        ))}
      </select>

      {/* VENDEDOR */}
      <select name="vendedor" value={formData.vendedor} onChange={handleChange} className="w-full mb-4 p-2 border rounded" required>
        <option value="">Seleccionar vendedor</option>
        {vendedores.map(v => (
          <option key={v._id} value={v._id}>
            {v.nombreApellido} – {v.usuario}
          </option>
        ))}
      </select>

      <button type="submit" className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700 w-full">
        Registrar Venta
      </button>
    </form>
  );
}

export default FormularioVenta;
