import React, { useState, useEffect } from "react";
import axios from "axios";
import FormularioProducto from "./components/FormularioProducto";
import ListadoProductos from "./components/ListadoProductos";
import FormularioVenta from "./components/FormularioVenta";
import FormularioCliente from "./components/FormularioCliente";

function App() {
  const [productos, setProductos] = useState([]);

  const fetchProductos = async () => {
    try {
      const res = await axios.get("http://localhost:5000/api/productos");
      setProductos(res.data);
    } catch (err) {
      console.error("Error al obtener productos", err);
    }
  };

  useEffect(() => {
    fetchProductos();
  }, []);

  return (
    <div className="min-h-screen bg-gray-100 p-8 max-w-2xl mx-auto">
      <h1 className="text-2xl font-bold text-blue-600 mb-6 text-center">Sistema de Facturaci√≥n</h1>

      <FormularioProducto fetchProductos={fetchProductos} />
      <FormularioVenta productos={productos} fetchProductos={fetchProductos} />
      <ListadoProductos productos={productos} />
    </div>
  );
}

export default App;
