const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post("http://localhost:5000/api/clientes", formData);
      alert("✅ Cliente guardado");
      setFormData({
        nombre: "",
        apellido: "",
        dni: "",
        celular: "",
        email: "",
        tipo: ""
      });
    } catch (err) {
      console.error("❌ Error al guardar cliente", err);
      alert("❌ Error al guardar cliente");
    }
  };
